con:config.script:
import groovy.sql.Sql

//connection Driver, username password, URL

def url ='jdbc:mysql://localhost:3306/category';

def user='root'
def password='root'
def driver ='com.mysql.jdbc.Driver'

// Register to MySQL  JDBC Driver - For Groovy to send and Recieve

com.eviware.soapui.support.GroovyUtils.registerJdbcDriver(driver)

//connect to SQL Instance

def sql = Sql.newInstance(url,user,password,driver)

sql.eachRow("select * from details")
{
	row ->
	def Category ="$row.category";
	def productkey ="$row.productkey";
	def quantity ="$row.quantity";
	testRunner.testCase.testSuite.testCases['TC027'].setPropertyValue("Category",Category)
     testRunner.testCase.testSuite.testCases['TC027'].setPropertyValue("productkey",productkey)
     testRunner.testCase.testSuite.testCases['TC027'].setPropertyValue("quantity",quantity)
   def tc=  testRunner.testCase.testSuite.testCases['TC027']
   tc.run(null, false)

}

sql.close()

