con:config.script:
import jxl.*
import eviware.soapui.model.*

filePath = "C:/Users/PoojaChintha/Desktop/pooja/Reskill_API/Project/REST Project/dataDrivens.xls"

File Excel = new File(filePath)

//Excel contains woekbook and workbook contains sheets
Workbook wb = Workbook.getWorkbook(Excel)
//fetch the sheet

Sheet sh =wb.getSheet("Sheet1")

// Getting row and column sizes

rowSize =sh.getRows().toInteger()
columnSize =sh.getColumns().toInteger()

/*log.info " Total number of rows " + rowSize
log.info " Total number of columns " + columnSize
def totalstep_count = 0*/

for(def i=1;i<rowSize; i++)
{

		email =sh.getCell(0,i).getContents()
		productkey=sh.getCell(1,i).getContents()
		log.info email
		log.info productkey
	testRunner.testCase.testSuite.testCases['TC028_29'].setPropertyValue("email",email)
	testRunner.testCase.testSuite.testCases['TC028_29'].setPropertyValue("productkey",productkey)


def tc = testRunner.testCase.testSuite.getTestCaseByName('TC028_29')
def runner=tc.run(null,false)

log.info "Status : $runner.status, time taken for testcase is $runner.timeTaken ms"

//number of test steps execuetd
 log.info "number of tets steps executed are : " +runner.results.size()
//Total number teststeps Executed
 //totalstep_count =totalstep_count+runner.results.size()
}

//log.info totalstep_count

